import React from "react";

export default function () {
    const depositHandler = async (amount) => {
        console.log(amount)
        const daiContract = new ethers.Contract(DAIContractAddress, DAIABI, signer)
        console.log(BetContractAddress)
        console.log(DAIContractAddress)
        const approvetx = await daiContract.approve(BetContractAddress, 100000)
        await approvetx.wait()
        console.log('Approval:', approvetx)

        const betContract = new ethers.Contract(
          BetContractAddress,
          BetABI,
          signer
        )
        const tx = await betContract.deposit(DAIContractAddress,100000)
        console.log('Deposit:', tx)
      }
  return (
    <div className="depositCard">
      <h3>Deposit DAI</h3>
      <input type="number" placeholder="Amount" />
      <button onClick={() => depositHandler(1)}>Deposit</button>
    </div>
  );
}
